"use strict";
/* eslint-disable no-template-curly-in-string */
const RELEASE_VERSION_NAME = 'chore(release): ${version}';
const CHANGELOG_PATTERN = 'git log --pretty=format:"* %s (%h)" ${from}...${to} --no-merges --invert-grep --grep=chore';
/* eslint-enable no-template-curly-in-string */
const CHANGELOG_PRESET = 'angular';
const CHANGELOG_INFILE = 'CHANGELOG.md';
const CALVER_FORMAT = 'YYYY.MM.MICRO';
const GLOB_NUMBER = '[0-9]';
module.exports = {
    git: {
        commitMessage: RELEASE_VERSION_NAME,
        changelog: CHANGELOG_PATTERN,
        requireCommits: true,
        requireUpstream: false,
        tagMatch: [
            // Year
            GLOB_NUMBER.repeat(4),
            // Month
            `${GLOB_NUMBER}*`,
            // Micro
            '*'
        ].join('.')
    },
    github: {
        release: true
    },
    npm: {
        publish: false
    },
    plugins: {
        '@justia/releaser/dist/plugins/conventional-changelog.js': {
            preset: CHANGELOG_PRESET,
            infile: CHANGELOG_INFILE
        },
        '@justia/releaser/dist/plugins/github.js': {
            usePr: true,
            automergePr: true
        },
        '@justia/releaser/dist/plugins/git-config.js': {
            removeVersionBranch: true
        }
    },
    justia: {
        fallbackVersion: '0.0.0',
        baseBranch: 'master',
        headBranch: 'develop',
        useVersionBranch: true,
        calverFormat: CALVER_FORMAT
    }
};
